,p_plug_display_sequence=>80
,p_include_in_reg_disp_sel_yn=>'N'
,p_plug_display_point=>'BODY'
,p_plug_source=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'SELECT',
'  user_notif_pref_id,',
'  INITCAP(REPLACE(REPLACE(role_name, ''SV_SERT_'', NULL),''_'','' '')) role,',
'  CASE ',
'    WHEN role_name LIKE ''%APPROVER%'' THEN ''When other exceptions are ready to be approved''',
'    WHEN role_name LIKE ''%EVALUATOR%'' THEN ''When my exceptions are approved/rejected''',
'  END condition,',
'  NVL(workspace, ''** All Workspaces **'') workspace,',
'  NVL(new_exception_notify,''NEVER'') new_exception_notify,',
'  TO_DATE(time_of_day,''HH24'') time,',
'  CASE ',
'    WHEN user_notif_pref_id IS NULL THEN',
'      apex_util.prepare_url(''f?p=&APP_ID.:2015:&APP_SESSION.:::2015:P2015_WORKSPACE_ID,P2015_ROLE_NAME:'' || workspace_id ||',
'        '','' || role_name)',
'    ELSE ',
'      apex_util.prepare_url(''f?p=&APP_ID.:2015:&APP_SESSION.:::2015:P2015_WORKSPACE_ID,P2015_USER_NOTIF_PREF_ID:'' ||',
'        workspace_id || '','' || user_notif_pref_id)',
'    END url',
'FROM',
'  (',
'  -- Include any users mapped in both ROLES and NOTIF tables',
'  SELECT',
'    unp.user_notif_pref_id,',
'    ur.role_name,',
'    w.workspace,',
'    ur.workspace_id,',
'    unp.new_exception_notify,',
'    unp.time_of_day',
'  FROM ',
'    sv_sec_user_roles ur,',
'    sv_sec_user_notif_prefs unp,',
'    apex_workspaces w',
'  WHERE',
'    ur.user_name = :APP_USER',
'    AND ur.user_workspace_id = :G_WORKSPACE_ID',
'    AND ur.user_name||ur.user_workspace_id||ur.role_name||ur.workspace_id = unp.user_name||unp.user_workspace_id||unp.role_name||unp.workspace_id',
'    AND ur.role_name IN (''SV_SERT_APPROVER'',''SV_SERT_APPROVER_ALL'',''SV_SERT_EVALUATOR'',''SV_SERT_EVALUATOR_SCHEDULER_ALL'')',
'    AND ur.workspace_id = w.workspace_id(+)',
'  UNION',
'  -- Include any users who are only mapped in NOTIF table',
'  SELECT',
'    unp.user_notif_pref_id,',
'    unp.role_name,',
'    w.workspace,',
'    unp.workspace_id,',
'    unp.new_exception_notify,',
'    unp.time_of_day',
'  FROM ',
'    sv_sec_user_notif_prefs unp,',
'    apex_workspaces w',
'  WHERE',
'    unp.user_name||unp.workspace_id||unp.user_workspace_id = :APP_USER||:G_WORKSPACE_ID||:G_WORKSPACE_ID',
'    AND unp.role_name IN (''SV_SERT_EVALUATOR'')',
'    AND unp.workspace_id = w.workspace_id',
'  -- Union in ROLE-only and default assignments',
'  UNION',
'  SELECT',
'    NULL user_notif_pref_id,',
'    role_name,',
'    workspace,',
'    workspace_id,',
'    NULL new_exception_notify,',
'    NULL time_of_day',
'  FROM',
'    (',
'      (',
'      -- Include any users with only ROLES mappings',
'      SELECT',
'        ur.role_name,',
'        w.workspace,',
'        ur.workspace_id',
'      FROM ',
'        sv_sec_user_roles ur,',
'        apex_workspaces w',
'      WHERE',
'        ur.user_name = :APP_USER',
'        AND ur.user_workspace_id = :G_WORKSPACE_ID',
'        AND ur.role_name IN (''SV_SERT_APPROVER'',''SV_SERT_APPROVER_ALL'',''SV_SERT_EVALUATOR'',''SV_SERT_EVALUATOR_SCHEDULER_ALL'')',
'        AND ur.workspace_id = w.workspace_id(+)',
'      UNION ALL',
'      -- Include a workspace user with rows in neither table as an EVALUATOR by default',
'      SELECT',
'        ''SV_SERT_EVALUATOR'' role_name,',
'        w.workspace,',
'        TO_NUMBER(:G_WORKSPACE_ID) workspace_id',
'      FROM ',
'        apex_workspaces w',
'      WHERE',
'        w.workspace_id = :G_WORKSPACE_ID',
'        AND 0 = ',
'        (',
'        SELECT COUNT(*) ',
'          FROM sv_sec_user_notif_prefs unp, apex_workspaces w',
'          WHERE unp.user_name||unp.workspace_id||unp.user_workspace_id = :APP_USER||:G_WORKSPACE_ID||:G_WORKSPACE_ID',
'            AND unp.role_name IN (''SV_SERT_EVALUATOR'')',
'            AND unp.workspace_id = w.workspace_id',
'        ) ',
'      )',
'    MINUS',
'    -- Remove ',
'    SELECT',
'      ur.role_name,',
'      w.workspace,',
'      ur.workspace_id',
'    FROM ',
'      sv_sec_user_roles ur,',
'      sv_sec_user_notif_prefs unp,',
'      apex_workspaces w',
'    WHERE',
'      ur.user_name = :APP_USER',
'      AND ur.user_workspace_id = :G_WORKSPACE_ID',
'      AND ur.user_name||ur.user_workspace_id||ur.role_name||ur.workspace_id = unp.user_name||unp.user_workspace_id||unp.role_name||unp.workspace_id',
'      AND ur.role_name IN (''SV_SERT_APPROVER'',''SV_SERT_APPROVER_ALL'',''SV_SERT_EVALUATOR'',''SV_SERT_EVALUATOR_SCHEDULER_ALL'')',
'      AND ur.workspace_id = w.workspace_id(+)',
'    )',
'  )'))
,p_plug_source_type=>'NATIVE_IR'
,p_plug_query_row_template=>1
,p_plug_query_options=>'DERIVED_REPORT_COLUMNS'
,p_prn_content_disposition=>'ATTACHMENT'
